#!/usr/bin/env python3
from flask import Flask, request, redirect
import os
import subprocess
import sys
import yaml
import MySQLdb

app = Flask(__name__)

# Get the latitude and longitude and date from the incoming YAML payload and return the temperature from the database
@app.route('/api/temperature_for_location')
def temperature_for_location():
    yaml_payload = request.args.get('payload', None)
    if yaml_payload is None:
        return 'No payload provided'
    try:
        payload = yaml.safe_load(yaml_payload)
    except yaml.YAMLError as exc:
        return 'Invalid YAML payload'
    if 'latitude' not in payload or 'longitude' not in payload or 'date' not in payload:
        return 'Invalid payload'
    latitude = payload['latitude']
    longitude = payload['longitude']
    date = payload['date']
    try:
        db = MySQLdb.connect(host='mysql', user='root', passwd='root', db='weather')
        cursor = db.cursor()
        cursor.execute('SELECT temperature FROM temperature WHERE latitude = %s AND longitude = %s AND date = %s', (latitude, longitude, date))
        temperature = cursor.fetchone()
        db.close()
        if temperature is None:
            return 'No temperature found'
        return str(temperature[0])
    except MySQLdb.Error as e:
        return 'Error connecting to database:'+ str(e)

#!/usr/bin/env python3
from flask import Flask, request, redirect
import os
import subprocess
import sys
import yaml
import MySQLdb

app = Flask(__name__)

# Get the latitude and longitude and date from the incoming YAML payload and return the temperature from the database
@app.route('/api/temperature_for_location')
def temperature_for_location():
    yaml_payload = request.args.get('payload', None)
    if yaml_payload is None:
        return 'No payload provided'
    try:
        payload = yaml.safe_load(yaml_payload)
    except yaml.YAMLError as exc:
        return 'Invalid YAML payload'
    if 'latitude' not in payload or 'longitude' not in payload or 'date' not in payload:
        return 'Invalid payload'
    latitude = payload['latitude']
    longitude = payload['longitude']
    date = payload['date']
    db = MySQLdb.connect(host='localhost', user='root', passwd='', db='weather')
    cursor = db.cursor()
    cursor.execute('SELECT temperature FROM temperature WHERE latitude = %s AND longitude = %s AND date = %s', (latitude, longitude, date))
    result = cursor.fetchone()
    if result is None:
        return 'No temperature found'
    return str(result[0])

#!/usr/bin/env python3
from flask import Flask, request, redirect
import os
import subprocess
import sys
import yaml

app = Flask(__name__)

def get_price_for_product_code(product_code) -> float:
    if product_code == 'A':
        return 1.0
    elif product_code == 'B':
        return 2.0
    else:
        return 0.0

# Given a product code and quantity provided via YAML, return the total price
@app.route('/api/calculate_price')
def calculate_price():
    yaml_payload = request.args.get('payload', None)
    if yaml_payload is None:
        return 'No payload provided', 400

    try:
        payload = yaml.safe_load(yaml_payload)
    except yaml.YAMLError as exc:
        return 'Invalid YAML payload', 400

    product_code = payload.get('product_code', None)
    quantity = payload.get('quantity', None)

    if product_code is None or quantity is None:
        return 'Missing product_code or quantity', 400

    price = get_price_for_product_code(product_code) * quantity
    return str(price), 200

