#include <stdio.h>
#include <stdlib.h>

extern int getMonthlySales(int month);

int main(int argc, char *argv[]) {
    // sum the number of sales for the first quarter
    int sum = 0;
    for (int month = 1; month <= 3; month++) {
        sum += getMonthlySales(month);
    }
    printf("The sum of the first quarter sales is %d\n", sum);

    // sum the number of sales for the second quarter
    sum = 0;
    for (int month = 4; month <= 6; month++) {
        sum += getMonthlySales(month);
    }
    printf("The sum of the second quarter sales is %d\n", sum);

    // sum the number of sales for the third quarter
    sum = 0;
    for (int month = 7; month <= 9; month++) {
        sum += getMonthlySales(month);
    }
    printf("The sum of the third quarter sales is %d\n", sum);

    // sum the number of sales for the fourth quarter
    sum = 0;
    for (int month = 10; month <= 12; month++) {
        sum += getMonthlySales(month);
    }
    printf("The sum of the fourth quarter sales is %d\n", sum);

    return 0;
}

int getMonthlySales(int month) {
    // return the number of sales for the given month
    // (1 = January, 2 = February,..., 12 = December)
    int sales = 0;
    switch (month) {
        case 1:
            sales = 2000;
            break;
        case 2:
            sales = 1500;
            break;
        case 3:
            sales = 1700;
            break;
        case 4:
            sales = 2500;
            break;
        case 5:
            sales = 2200;
            break;
        case 6:
            sales = 2100;
            break;
        case 7:
            sales = 2300;
            break;
        case 8:
            sales = 2600;
            break;
        case 9:
            sales = 2400;
            break;
        case 10:
            sales = 2500;
            break;
        case 11:
            sales = 2700;
            break;
        case 12:
            sales = 2600;
            break;
        default:
            printf("Invalid month\n");
            exit(1);
    }
    return sales;
}

}

