    printf("The value at index 4 is %d\n", getValueFromArray(array, size, 4));
    printf("The value at index 5 is %d\n", getValueFromArray(array, size, 5));
    printf("The value at index -1 is %d\n", getValueFromArray(array, size, -1));
    printf("The value at index 100 is %d\n", getValueFromArray(array, size, 100));

    return 0;
}
