#include <stdio.h>
#include <stdlib.h>

#define IDS_ARRAY_SIZE 10
int ids[IDS_ARRAY_SIZE];

int getIdFromArray(int index) {
    // given the array of ids, return the value at the given index and -1 if the index is out of bounds
    if (index < 0 || index >= IDS_ARRAY_SIZE) {
        return -1;
    }
    return ids[index];
}

int main() {
    int index = 0;
    int id = 0;

    // populate the array with some values
    for (int i = 0; i < IDS_ARRAY_SIZE; i++) {
        ids[i] = i;
    }

    // get the first id
    id = getIdFromArray(index);
    printf("The first id is %d\n", id);

    // get the last id
    index = IDS_ARRAY_SIZE - 1;
    id = getIdFromArray(index);
    printf("The last id is %d\n", id);

    // get an id that is out of bounds
    index = IDS_ARRAY_SIZE;
    id = getIdFromArray(index);
    printf("The id at index %d is %d\n", index, id);

    return 0;
}

}

