#include <stdio.h>
#include <stdlib.h>

typedef struct {
    char name[100];
    int status;
} person;

int main(int argc, char *argv[]) {
    // allocate a new person
    person *p = (person *)malloc(sizeof(person));
    // set status to 0
    p->status = 0;
    // set name to "John Doe"
    strcpy(p->name, "John Doe");
    // print name and status
    printf("Name: %s\n", p->name);
    printf("Status: %d\n", p->status);
    // free the person
    free(p);
    // set status to 1
    p->status = 1;
    // print name and status
    printf("Name: %s\n", p->name);
    printf("Status: %d\n", p->status);
    return 0;
}
}

