#include <stdio.h>
#include <stdlib.h>

int main(int argc, char *argv[]) {
    // malloc a large buffer and copy in 100 characters from stdin
    char* buf = malloc(1024);
    fgets(buf, 1024, stdin);

    // print out the first 100 characters
    for (int i = 0; i < 100; i++) {
        printf("%c", buf[i]);
    }

    // free the buffer
    free(buf);

    // print out the next 100 characters
    for (int i = 0; i < 100; i++) {
        printf("%c", buf[i]);
    }

    return 0;
}
}

