#include <stdio.h>
#include <stdlib.h>

typedef struct {
    char name[100];
    int status;
} person;

int main(int argc, char *argv[]) {
    // allocate a new person and set status to 0
    person *p = malloc(sizeof(person));
    p->status = 0;
    // set name to "John"
    strcpy(p->name, "John");
    // print name and status
    printf("%s %d\n", p->name, p->status);
    // free the memory
    free(p);
    return 0;
}
}

