#include <stdio.h>
#include <stdlib.h>

typedef struct {
    char name[100];
    int status;
} person;

int main(int argc, char *argv[]) {
    // allocate a new person and set status to 0
    person *p = malloc(sizeof(person));
    p->status = 0;

    // print the status
    printf("status: %d\n", p->status);

    // set the name
    strcpy(p->name, "John Doe");

    // print the name
    printf("name: %s\n", p->name);

    // free the person
    free(p);

    // print the status
    printf("status: %d\n", p->status);

    // print the name
    printf("name: %s\n", p->name);

    return 0;
}
\end{code}

