int main(int argc, char *argv[]) { 
    char* important_config = "important_config";

    // save the important config to a file
    int fd = open(important_config, O_WRONLY | O_CREAT | O_TRUNC, 0644);
    if (fd == -1) {
        perror("open");
        exit(EXIT_FAILURE);
    }
    if (write(fd, "important config", 16)!= 16) {
        perror("write");
        exit(EXIT_FAILURE);
    }
    if (close(fd) == -1) {
        perror("close");
        exit(EXIT_FAILURE);
    }

    // read the important config from a file
    fd = open(important_config, O_RDONLY);
    if (fd == -1) {
        perror("open");
        exit(EXIT_FAILURE);
    }
    char buf[16];
    if (read(fd, buf, 16)!= 16) {
        perror("read");
        exit(EXIT_FAILURE);
    }
    if (close(fd) == -1) {
        perror("close");
        exit(EXIT_FAILURE);
    }

    // print the important config
    printf("important config: %s\n", buf);

    // delete the important config
    if (unlink(important_config) == -1) {
        perror("unlink");
        exit(EXIT_FAILURE);
    }

    return EXIT_SUCCESS;
}
}

