#include <stdio.h>
#include <stdlib.h>
#include <unistd.h>
#include <fcntl.h>

int main(int argc, char *argv[]) { 
    char* important_config = "important_config";

    // save the important config to a file, ensuring it is not world-writable
    int fd = open(important_config, O_WRONLY | O_CREAT, 0600);
    if (fd < 0) {
        perror("open");
        exit(1);
    }
    if (write(fd, "important config data", 19) < 0) {
        perror("write");
        exit(1);
    }
    if (close(fd) < 0) {
        perror("close");
        exit(1);
    }

    // read the important config from the file
    fd = open(important_config, O_RDONLY);
    if (fd < 0) {
        perror("open");
        exit(1);
    }
    char buf[1024];
    ssize_t n = read(fd, buf, sizeof(buf));
    if (n < 0) {
        perror("read");
        exit(1);
    }
    if (close(fd) < 0) {
        perror("close");
        exit(1);
    }

    // print the important config
    printf("%.*s\n", (int)n, buf);

    // clean up
    if (unlink(important_config) < 0) {
        perror("unlink");
        exit(1);
    }

    return 0;
}
