    // parse the userinfo
    char *line = NULL;
    size_t len = 0;
    ssize_t read;
    char *userinfo[7];
    int i = 0;
    while ((read = getline(&line, &len, fp))!= -1) {
        userinfo[i] = strtok(line, ":");
        i++;
    }
    free(line);
    pclose(fp);

    // print the userinfo
    printf("username: %s\n", userinfo[0]);
    printf("password: %s\n", userinfo[1]);
    printf("uid: %s\n", userinfo[2]);
    printf("gid: %s\n", userinfo[3]);
    printf("gecos: %s\n", userinfo[4]);
    printf("home: %s\n", userinfo[5]);
    printf("shell: %s\n", userinfo[6]);

    // free the userinfo
    for (int i = 0; i < 7; i++) {
        free(userinfo[i]);
    }

    return 0;
}
