int main(int argc, char *argv[]) {
    // get the username from the command line
    char *username = argv[1];

    // get the userinfo from the system
    char command[100] = {0};
    sprintf(command, "getent passwd %s", username);
    FILE *fp = popen(command, "r");
    if (fp == NULL) {
        printf("Failed to run command\n");
        exit(1);
    }

    // parse the userinfo
    char *line = NULL;
    size_t len = 0;
    ssize_t read;
    char *delim = ":";
    char *token;
    char *userinfo[7];
    int i = 0;
    while ((read = getline(&line, &len, fp))!= -1) {
        token = strtok(line, delim);
        while (token!= NULL) {
            userinfo[i++] = token;
            token = strtok(NULL, delim);
        }
    }
    free(line);
    pclose(fp);

    // print the userinfo
    printf("Username: %s\n", userinfo[0]);
    printf("Password: %s\n", userinfo[1]);
    printf("UID: %s\n", userinfo[2]);
    printf("GID: %s\n", userinfo[3]);
    printf("Gecos: %s\n", userinfo[4]);
    printf("Home: %s\n", userinfo[5]);
    printf("Shell: %s\n", userinfo[6]);

    // get the user's home directory
    char *home = userinfo[5];

    // get the user's.bashrc file
    char *bashrc = malloc(strlen(home) + 8);
    sprintf(bashrc, "%s/.bashrc", home);

    // open the.bashrc file
    int fd = open(bashrc, O_RDONLY);
    if (fd == -1) {
        printf("Failed to open.bashrc\n");
        exit(1);
    }

    // read the.bashrc file
    char *buffer = malloc(1024);
    int bytes_read = read(fd, buffer, 1024);
    if (bytes_read == -1) {
        printf("Failed to read.bashrc\n");
        exit(1);
    }
    buffer[bytes_read] = '\0';

    // print the.bashrc file
    printf("Contents of.bashrc:\n%s\n", buffer);

    // clean up
    free(bashrc);
    free(buffer);
    return 0;
}
