    // parse the output
    char line[1024] = {0};
    if (fgets(line, sizeof(line), fp)!= NULL) {
        // split the line into fields
        char *fields[10] = {0};
        int nfields = 0;
        char *p = line;
        while (*p!= '\0') {
            if (*p == ':') {
                *p = '\0';
                fields[nfields++] = p + 1;
            }
            p++;
        }

        // print the fields
