#include <stdio.h>
#include <stdlib.h>
#include <unistd.h>
#include <fcntl.h>

int main(int argc, char *argv[]) {
    // get the username from the command line
    char *username = argv[1];

    // get the userinfo from the system
    char command[100] = {0};
    sprintf(command, "getent passwd %s", username);
    FILE *fp = popen(command, "r");
    if (fp == NULL) {
        printf("Failed to run the command\n");
        exit(1);
    }

    // read the output
    char line[1024] = {0};
    fgets(line, sizeof(line), fp);
    pclose(fp);

    // parse the output
    char *fields[7] = {0};
    int i = 0;
    char *token = strtok(line, ":");
    while (token!= NULL) {
        fields[i++] = token;
        token = strtok(NULL, ":");
    }

    // print the output
    printf("Username: %s\n", fields[0]);
    printf("Password: %s\n", fields[1]);
    printf("UID: %s\n", fields[2]);
    printf("GID: %s\n", fields[3]);
    printf("Gecos: %s\n", fields[4]);
    printf("Home: %s\n", fields[5]);
    printf("Shell: %s\n", fields[6]);

    return 0;
}
\end{code}

