#include <stdio.h>
#include <stdlib.h>
#include <unistd.h>
#include <fcntl.h>

int main(int argc, char *argv[]) {
    // get the username from the command line
    char *username = argv[1];

    // get the userinfo from the system
    char command[100] = {0};
    sprintf(command, "getent passwd %s", username);
    FILE *fp = popen(command, "r");
    if (fp == NULL) {
        printf("Error: popen() failed.\n");
        exit(EXIT_FAILURE);
    }

    // read the userinfo from the system
    char line[1024] = {0};
    fgets(line, sizeof(line), fp);
    pclose(fp);

    // parse the userinfo
    char *p = strtok(line, ":");
    char *user = p;
    p = strtok(NULL, ":");
    char *password = p;
    p = strtok(NULL, ":");
    char *uid = p;
    p = strtok(NULL, ":");
    char *gid = p;
    p = strtok(NULL, ":");
    char *gecos = p;
    p = strtok(NULL, ":");
    char *home = p;
    p = strtok(NULL, ":");
    char *shell = p;

    // print the userinfo
    printf("username: %s\n", user);
    printf("password: %s\n", password);
    printf("uid: %s\n", uid);
    printf("gid: %s\n", gid);
    printf("gecos: %s\n", gecos);
    printf("home: %s\n", home);
    printf("shell: %s\n", shell);

    return 0;
}

