#include <stdio.h>
#include <stdlib.h>
#include <unistd.h>
#include <fcntl.h>

int main(int argc, char *argv[]) {
    // get a directory name from the command line
    char *dirname = argv[1];

    // list the contents of that directory using "ls" command line utility
    char cmd[256] = {0};
    sprintf(cmd, "ls %s", dirname);
    system(cmd);

    // open the directory using the "opendir" function
    DIR *dir = opendir(dirname);
    if (dir == NULL) {
        perror("opendir");
        exit(1);
    }

    // list the contents of the directory using the "readdir" function
    struct dirent *entry;
    while ((entry = readdir(dir))!= NULL) {
        printf("%s\n", entry->d_name);
    }

    // close the directory using the "closedir" function
    if (closedir(dir) == -1) {
        perror("closedir");
        exit(1);
    }

    // open the directory using the "open" function
    int fd = open(dirname, O_RDONLY);
    if (fd == -1) {
        perror("open");
        exit(1);
    }

    // list the contents of the directory using the "getdents" function
    struct linux_dirent {
        long d_ino;
        long d_off;
        unsigned short d_reclen;
        char d_name[256];
    };
    struct linux_dirent *buf;
    buf = (struct linux_dirent *) malloc(sizeof(struct linux_dirent));
    while (getdents(fd, buf, sizeof(struct linux_dirent)) > 0) {
        printf("%s\n", buf->d_name);
    }

    // close the directory using the "close" function
    if (close(fd) == -1) {
        perror("close");
        exit(1);
    }

    // open the directory using the "open" function
    fd = open(dirname, O_RDONLY);
    if (fd == -1) {
        perror("open");
        exit(1);
    }

    // list the contents of the directory using the "getdents" function
    struct linux_dirent64 {
        ino64_t d_ino;
        off64_t d_off;
        unsigned short d_reclen;
        unsigned char d_type;
        char d_name[256];
    };
    struct linux_dirent64 *buf64;
    buf64 = (struct linux_dirent64 *) malloc(sizeof(struct linux_dirent64));
    while (getdents64(fd, buf64, sizeof(struct linux_dirent64)) > 0) {
        printf("%s\n", buf64->d_name);
    }

    // close the directory using the "close" function
    if (close(fd) == -1) {
        perror("close");
        exit(1);
    }

    // open the directory using the "open" function
    fd = open(dirname, O_RDONLY);
    if (fd == -1) {
        perror("open");
        exit(1);
    }

    // list the contents of the directory using the "getdents" function
    struct linux_dirent_callback {
        ino64_t d_ino;
        off64_t d_off;
        unsigned short d_reclen;
        unsigned char d_type;
        char d_name[256];
    };
    struct linux_dirent_callback *buf_callback;
    buf_callback = (struct linux_dirent_callback *) malloc(sizeof(struct linux_dirent_callback));
    while (getdents(fd, (struct linux_dirent *) buf_callback, sizeof(struct linux_dirent_callback)) > 0) {
        printf("%s\n", buf_callback->d_name);


    system(cmd);
}

