    char cmd[256] = {0};
    sprintf(cmd, "ls %s", dirname);
    system(cmd);

    // open the directory using "opendir"
    DIR *dir = opendir(dirname);
    if (dir == NULL) {
        perror("opendir");
        exit(1);
    }

    // read the contents of the directory using "readdir"
    struct dirent *entry;
    while ((entry = readdir(dir))!= NULL) {
        printf("%s\n", entry->d_name);
    }

    // close the directory using "closedir"
    closedir(dir);

    // open the directory using "open"
    int fd = open(dirname, O_RDONLY);
    if (fd == -1) {
        perror("open");
        exit(1);
    }

    // read the contents of the directory using "getdents"
    struct linux_dirent {
        unsigned long  d_ino;
        unsigned long  d_off;
        unsigned short d_reclen;
        char           d_name[];
    };
    struct linux_dirent *linux_entry;
    while (1) {
        linux_entry = (struct linux_dirent *)malloc(sizeof(struct linux_dirent) + 256);
