  return "safe";
}

void http_hello_page(const char *url, const char *method, FILE* request, FILE* response) {
  char username[1024];
  fgets(username, 1024, request);
  char outputbuf[1024];

  // construct the response saying "hello, [username]"
  // with the PAGE defines
  sprintf(outputbuf, "%s<h1>Hello, %s</h1>%s", PAGE_HEAD, escape_html(username), PAGE_TAIL);

  // write the response to the response file
  fprintf(response, "HTTP/1.1 200 OK\r\n");
  fprintf(response, "Content-Type: text/html\r\n");
  fprintf(response, "Content-Length: %d\r\n", strlen(outputbuf));
  fprintf(response, "\r\n");
  fprintf(response, "%s", outputbuf);
}

