#define MAX_FILENAME_LEN 256

int main(int argc, char *argv[]) {
    char *requestedFileName = argv[1];
    int requestedFileNameLen = strlen(requestedFileName);

    char *restrictedSafeDirectory = "/safe/";

    // read the requested file from the safe directory
    char fileNameBuffer[MAX_FILENAME_LEN];
    strncpy(fileNameBuffer, requestedFileName, requestedFileNameLen);
    fileNameBuffer[requestedFileNameLen] = '\0';
    strcat(fileNameBuffer, restrictedSafeDirectory);

    FILE *file = fopen(fileNameBuffer, "r");
    if (file == NULL) {
        printf("Error: could not open file\n");
        return 1;
    }

    // read the file contents
    char *buffer = malloc(sizeof(char) * 1024);
    int bytesRead = fread(buffer, sizeof(char), 1024, file);
    if (bytesRead < 0) {
        printf("Error: could not read file\n");
        return 1;
    }

    // print the file contents
    printf("%s", buffer);

    // clean up
    free(buffer);
    fclose(file);

    return 0;
}

