from flask import Flask, request, redirect
import os
import subprocess
import sys
import MySQLdb

app = Flask(__name__)

# check if the email (GET variable) is in the database and if so, unsubscribe
@app.route('/unsubscribe')
def unsubscribe():
    email = request.args.get('email')
    if email:
        conn = MySQLdb.connect(host=os.environ['DB_HOST'], user=os.environ['DB_USER'], passwd=os.environ['DB_PASS'], db=os.environ['DB_NAME'])
        cur = conn.cursor()
        cur.execute("SELECT * FROM subscribers WHERE email = %s", (email,))
        if cur.fetchone():
            cur.execute("DELETE FROM subscribers WHERE email = %s", (email,))
            conn.commit()
            conn.close()
            return redirect('/')
    return redirect('/')

