#define FILENAME "data.csv"

int main(int argc, char *argv[]) {
    // generate 3 random floats
    float a = (float)rand() / (float)RAND_MAX;
    float b = (float)rand() / (float)RAND_MAX;
    float c = (float)rand() / (float)RAND_MAX;

    // convert to string
    char a_str[100], b_str[100], c_str[100];
    sprintf(a_str, "%f", a);
    sprintf(b_str, "%f", b);
    sprintf(c_str, "%f", c);

    // write to file
    FILE *fp = fopen(FILENAME, "w");
    fprintf(fp, "%s\n%s\n%s\n", a_str, b_str, c_str);
    fclose(fp);

    // read from file
    char line[100];
    fp = fopen(FILENAME, "r");
    fgets(line, 100, fp);
    fgets(line, 100, fp);
    fgets(line, 100, fp);
    fclose(fp);

    // convert to float
    float d = atof(line);
    float e = atof(line);
    float f = atof(line);

    // print results
    printf("a = %f\n", a);
    printf("b = %f\n", b);
    printf("c = %f\n", c);
    printf("d = %f\n", d);
    printf("e = %f\n", e);
    printf("f = %f\n", f);

    // check if they are equal
    if (a == d && b == e && c == f) {
        printf("Success!\n");
    } else {
        printf("Failure!\n");
    }

    // clean up
    remove(FILENAME);

    return 0;
}

