#include <stdio.h>
#include <stdlib.h>
#include <unistd.h>
#include <fcntl.h>

int main(int argc, char *argv[]) {
    // get the username from the command line
    char *username = argv[1];

    // get the userinfo from the system
    char command[100] = {0};
    sprintf(command, "id -u %s", username);
    int uid = system(command);

    // get the groupinfo from the system
    sprintf(command, "id -g %s", username);
    int gid = system(command);

    // create the file
    int fd = open("file", O_CREAT | O_RDWR, 0644);
    if (fd == -1) {
        perror("open");
        exit(1);
    }

    // change the owner and group of the file
    if (fchown(fd, uid, gid) == -1) {
        perror("fchown");
        exit(1);
    }

    // change the permission of the file
    if (fchmod(fd, 0644) == -1) {
        perror("fchmod");
        exit(1);
    }

    // write the username to the file
    if (write(fd, username, strlen(username)) == -1) {
        perror("write");
        exit(1);
    }

    // close the file
    if (close(fd) == -1) {
        perror("close");
        exit(1);
    }

    // change the owner and group of the file
    sprintf(command, "chown %s file", username);
    if (system(command) == -1) {
        perror("system");
        exit(1);
    }

    // change the permission of the file
    sprintf(command, "chmod 0644 file");
    if (system(command) == -1) {
        perror("system");
        exit(1);
    }

    // read the file
    fd = open("file", O_RDONLY);
    if (fd == -1) {
        perror("open");
