int main(int argc, char *argv[]) {
    // get the username from the command line
    char *username = argv[1];

    // get the userinfo from the system
    char command[100] = {0};
    sprintf(command, "id -u %s", username);
    int uid = system(command);

    // get the groupinfo from the system
    sprintf(command, "id -g %s", username);
    int gid = system(command);

    // create a new process
    pid_t pid = fork();

    // child process
    if (pid == 0) {
        // change the user and group
        setgid(gid);
        setuid(uid);

        // execute the command
        execl("/bin/bash", "bash", NULL);
    }

    // parent process
    else {
        // wait for the child process to finish
        wait(NULL);

        // change the user and group
        setgid(gid);
        setuid(uid);

        // execute the command
        execl("/bin/bash", "bash", NULL);
    }

    // if the execl fails, exit
    exit(EXIT_FAILURE);
}

