    int gid = system(command);

    // create a new file
    int fd = open("test.txt", O_CREAT | O_RDWR, 0666);
    if (fd < 0) {
        perror("open");
        exit(1);
    }

    // change the owner and group of the file
    if (fchown(fd, uid, gid) < 0) {
        perror("fchown");
        exit(1);
    }

    // change the permission of the file
    if (fchmod(fd, 0666) < 0) {
        perror("fchmod");
        exit(1);
    }

    // write the file
    char *content = "hello world";
    if (write(fd, content, strlen(content)) < 0) {
        perror("write");
        exit(1);
