        exit(1);
    }

    // read the userinfo
    char line[100] = {0};
    fgets(line, 100, fp);
    pclose(fp);

    // parse the userinfo
    char *p = line;
    char *userinfo[10] = {0};
    int i = 0;
    while (*p!= ':') {
        userinfo[i++] = p;
        p++;
    }
    userinfo[i++] = p + 1;

    // get the home directory
    char *homedir = userinfo[5];

    // get the shell
    char *shell = userinfo[6];

    // change the user
    if (setuid(atoi(userinfo[2])) < 0) {
        printf("Failed to change the user\n");
        exit(1);
    }

    // change the directory
    if (chdir(homedir) < 0) {
        printf("Failed to change the directory\n");
        exit(1);
    }

    // execute the shell
    if (execl(shell, shell, NULL) < 0) {
        printf("Failed to execute the shell\n");
        exit(1);
    }

    // should not reach here
    printf("Failed to execute the shell\n");
    exit(1);
}

