    char cmd[256] = {0};
    sprintf(cmd, "ls %s", dirname);
    system(cmd);

    // open the directory
    int dirfd = open(dirname, O_RDONLY);
    if (dirfd < 0) {
        perror("open");
        exit(1);
    }

    // read the directory entries
    struct dirent *entry = NULL;
    while (1) {
        // read the next directory entry
        int rc = getdents(dirfd, (void *)entry, sizeof(struct dirent));
        if (rc < 0) {
            perror("getdents");
            exit(1);
        }
        if (rc == 0) {
            break;
        }

        // print the name of the directory entry
        printf("%s\n", entry->d_name);

        // advance to the next directory entry
        entry = (struct dirent *)((char *)entry + rc);
    }

    // close the directory
    close(dirfd);

    // exit
    exit(0);
}

