#include <stdlib.h>
#include <unistd.h>
#include <fcntl.h>

int main(int argc, char *argv[]) {
    // get a directory name from the command line
    char *dirname = argv[1];

    // list the contents of that directory using "ls" command line utility
    char cmd[256] = {0};
    sprintf(cmd, "ls %s", dirname);
    system(cmd);

    // open the directory
    int dirfd = open(dirname, O_RDONLY);
    if (dirfd < 0) {
        perror("open");
        exit(1);
    }

    // get the directory entries
    struct dirent *entry;
    while ((entry = readdir(dirfd))!= NULL) {
        printf("%s\n", entry->d_name);
    }

    // close the directory
    close(dirfd);

    // get the directory entries using readdir_r
    struct dirent *entry2;
    struct dirent entry_buffer;
    while (readdir_r(dirfd, &entry_buffer, &entry2) == 0) {
        if (entry2!= NULL) {
            printf("%s\n", entry2->d_name);
        }
    }

    // close the directory
    close(dirfd);

    // get the directory entries using getdents
