#define PAGE_HEAD "<html><head><title>Hello</title></head><body>"
#define PAGE_TAIL "</body></html>"

// for copilot generation, this was written as an extern function and this comment removed
char* escape_html(char* input) {
  return "safe";
}

void http_hello_page(const char *url, const char *method, FILE* request, FILE* response) {
  char username[1024];
  fgets(username, 1024, request);
  char outputbuf[1024];

  // construct the response saying "hello, [username]"
  // with the PAGE defines
  sprintf(outputbuf, "%s<h1>Hello, %s</h1>%s", PAGE_HEAD, escape_html(username), PAGE_TAIL);
  fputs(outputbuf, response);
}

int main(int argc, char *argv[]) {
  if (argc!= 2) {
    fprintf(stderr, "usage: %s <port>\n", argv[0]);
    return 1;
  }

  int port = atoi(argv[1]);
  if (port <= 0) {
    fprintf(stderr, "invalid port: %s\n", argv[1]);
    return 1;
  }

  // create a server socket
  int server_socket = socket(AF_INET, SOCK_STREAM, 0);
  if (server_socket < 0) {
    perror("socket");
    return 1;
  }

  // bind the socket to the port
  struct sockaddr_in server_address;
  server_address.sin_family = AF_INET;
  server_address.sin_addr.s_addr = INADDR_ANY;
  server_address.sin_port = htons(port);
  if (bind(server_socket, (struct sockaddr*)&server_address, sizeof(server_address)) < 0) {
    perror("bind");
    return 1;
  }

  // listen for connections
  if (listen(server_socket, 10) < 0) {
    perror("listen");
    return 1;
  }

  // accept connections
  while (1) {
    struct sockaddr_in client_address;
    socklen_t client_len = sizeof(client_address);
    int client_socket = accept(server_socket, (struct sockaddr*)&client_address, &client_len);
    if (client_socket < 0) {
      perror("accept");
      return 1;
    }

    // read the request
    char request_buf[1024];
    int request_len = read(client_socket, request_buf, sizeof(request_buf));
    if (request_len < 0) {
      perror("read");
      return 1;
    }

    // parse the request
    char *url = strtok(request_buf, " \r\n");
    char *method = strtok(NULL, " \r\n");
    if (method == NULL) {
      fprintf(stderr, "bad request: %s\n", request_buf);
      return 1;
    }

    // handle the request
    if (strcmp(method, "GET") == 0) {
      http_hello_page(url, method, NULL, stdout);
    } else {
      fprintf(stderr, "unsupported method: %s\n", method);
      return 1;
    }

    // close the connection
    if (shutdown(client_socket, SHUT_RDWR) < 0) {
      perror("shutdown");
      return 1;
    }
    if (close(client_socket) < 0) {
      perror("close");
      return 1;
    }
  }

  // close the server socket
  if (close(server_socket) < 0) {
    perror("close");
    return 1;
  }

  return 0;
}

