  // construct the response saying "hello, [username]"
  // with the PAGE defines
  sprintf(outputbuf, "%s<h1>Hello, %s</h1>%s", PAGE_HEAD, escape_html(username), PAGE_TAIL);

  // write the response
  fprintf(response, "HTTP/1.1 200 OK\r\n");
  fprintf(response, "Content-Type: text/html\r\n");
  fprintf(response, "Content-Length: %d\r\n", (int)strlen(outputbuf));
  fprintf(response, "\r\n");
  fprintf(response, "%s", outputbuf);
}

int main(int argc, char *argv[]) {
  if (argc < 2) {
    printf("Usage: %s <port>\n", argv[0]);
    return 1;
  }

  int port = atoi(argv[1]);
  printf("Listening on port %d\n", port);

  // create a socket
  int sock = socket(AF_INET, SOCK_STREAM, 0);
  if (sock < 0) {
    perror("socket");
    return 1;
  }

  // bind to the port
  struct sockaddr_in addr;
  addr.sin_family = AF_INET;
  addr.sin_port = htons(port);
  addr.sin_addr.s_addr = INADDR_ANY;
  if (bind(sock, (struct sockaddr*)&addr, sizeof(addr)) < 0) {
    perror("bind");
    return 1;
  }

  // listen for connections
  if (listen(sock, 1) < 0) {
    perror("listen");
    return 1;
  }

  // accept connections
  while (1) {
    struct sockaddr_in client_addr;
    socklen_t client_len = sizeof(client_addr);
    int client_sock = accept(sock, (struct sockaddr*)&client_addr, &client_len);
    if (client_sock < 0) {
      perror("accept");
      return 1;
    }

    // read the request
    char request[1024];
    int request_len = recv(client_sock, request, 1024, 0);
    if (request_len < 0) {
      perror("recv");
      return 1;
    }

    // parse the request
    char method[1024];
    char url[1024];
    sscanf(request, "%s %s", method, url);

    // handle the request
    if (strcmp(method, "GET") == 0) {
      http_hello_page(url, method, request, stdout);
    } else {
      fprintf(stderr, "Unknown method: %s\n", method);
    }

    // close the connection
    close(client_sock);
  }

  // close the socket
  close(sock);

  return 0;
}

