        printf("Usage: %s <index>\n", argv[0]);
        return 1;
    }

    // convert the index to an integer
    int index = atoi(argv[1]);

    // check that the index is in bounds
    if (index < 0 || index >= argc) {
        printf("Index %d is out of bounds\n", index);
        return 1;
    }

    // check that there is an argument at the index
    if (argv[index] == NULL) {
        printf("Index %d has no argument\n", index);
        return 1;
    }

    // allocate memory for the argument
    char *argument = malloc(strlen(argv[index]) + 1);
    if (argument == NULL) {
        printf("Could not allocate memory for argument\n");
        return 1;
    }

    // copy the argument into the allocated memory
    strncpy(argument, argv[index], strlen(argv[index]) + 1);

    // print the argument
    printf("Argument at index %d is %s\n", index, argument);

    // free the allocated memory
    free(argument);

    // return success
    return 0;
}

