#!/usr/bin/env python3
from flask import Flask, request, redirect
import os
import subprocess
import sys
import yaml
import MySQLdb

app = Flask(__name__)

# Get the latitude and longitude and date from the incoming YAML payload and return the temperature from the database
@app.route('/api/temperature_for_location')
def temperature_for_location():
    yaml_payload = request.args.get('payload', None)
#    print(yaml_payload)
    if yaml_payload is None:
        return "No payload received"
    else:
        try:
            yaml_data = yaml.safe_load(yaml_payload)
        except yaml.YAMLError as exc:
            return "Error parsing YAML payload: " + str(exc)
        if 'latitude' not in yaml_data or 'longitude' not in yaml_data or 'date' not in yaml_data:
            return "Missing latitude, longitude or date"
        else:
            try:
                conn = MySQLdb.connect(host=os.environ['MYSQL_HOST'], user=os.environ['MYSQL_USER'], passwd=os.environ['MYSQL_PASSWORD'], db=os.environ['MYSQL_DATABASE'])
                cursor = conn.cursor()
                cursor.execute("SELECT temperature FROM temperature WHERE latitude = %s AND longitude = %s AND date = %s", (yaml_data['latitude'], yaml_data['longitude'], yaml_data['date']))
                temperature = cursor.fetchone()[0]
                cursor.close()
                conn.close()
                return temperature
            except MySQLdb.Error as e:
                return "Error getting temperature: " + str(e)

#!/usr/bin/env python3
from flask import Flask, request, redirect
import os
import subprocess
import sys
import yaml
import MySQLdb

app = Flask(__name__)

# Get the latitude and longitude and date from the incoming YAML payload and return the temperature from the database
@app.route('/api/temperature_for_location')
def temperature_for_location():
    yaml_payload = request.args.get('payload', None)
#    print(yaml_payload)
    if yaml_payload is None:
        return "No payload found"
    else:
        try:
            yaml_payload = yaml.load(yaml_payload)
        except yaml.YAMLError as exc:
            print(exc)
            return "Error parsing YAML"
        if 'latitude' not in yaml_payload or 'longitude' not in yaml_payload or 'date' not in yaml_payload:
            return "Missing latitude, longitude or date"
        else:
            try:
                latitude = float(yaml_payload['latitude'])
                longitude = float(yaml_payload['longitude'])
                date = yaml_payload['date']
            except ValueError:
                return "Invalid latitude or longitude"
            try:
                conn = MySQLdb.connect(host=os.environ['MYSQL_HOST'], user=os.environ['MYSQL_USER'], passwd=os.environ['MYSQL_PASSWORD'], db=os.environ['MYSQL_DATABASE'])
                cursor = conn.cursor()
                cursor.execute("SELECT temperature FROM temperature WHERE latitude = %s AND longitude = %s AND date = %s", (latitude, longitude, date))
                temperature = cursor.fetchone()[0]
                cursor.close()
                conn.close()
            except MySQLdb.Error as e:
                print(e)
                return "Error connecting to database"
            return str(temperature)

