int main(int argc, char *argv[]) {
    // generate a random integer larger than 1000
    int n = rand() % 1000 + 1000;
    // generate a random integer between 1 and 1000
    int m = rand() % 1000 + 1;
    // generate a random integer between 1 and 1000
    int k = rand() % 1000 + 1;

    // generate a random array of integers between 1 and 1000
    int *arr = malloc(n * sizeof(int));
    for (int i = 0; i < n; i++) {
        arr[i] = rand() % 1000 + 1;
    }

    // generate a random array of integers between 1 and 1000
    int *arr2 = malloc(m * sizeof(int));
    for (int i = 0; i < m; i++) {
        arr2[i] = rand() % 1000 + 1;
    }

    // generate a random array of integers between 1 and 1000
    int *arr3 = malloc(k * sizeof(int));
    for (int i = 0; i < k; i++) {
        arr3[i] = rand() % 1000 + 1;
    }

    // print the array
    printf("Array 1: ");
    for (int i = 0; i < n; i++) {
        printf("%d ", arr[i]);
    }
    printf("\n");

    // print the array
    printf("Array 2: ");
    for (int i = 0; i < m; i++) {
        printf("%d ", arr2[i]);
    }
    printf("\n");

    // print the array
    printf("Array 3: ");
    for (int i = 0; i < k; i++) {
        printf("%d ", arr3[i]);
    }
    printf("\n");

    // merge the arrays
    int *arr4 = malloc((n + m + k) * sizeof(int));
    int i = 0, j = 0, k = 0;
    while (i < n && j < m && k < k) {
        if (arr[i] < arr2[j]) {
            arr4[k] = arr[i];
            i++;
        } else {
            arr4[k] = arr2[j];
            j++;
        }
        k++;
    }
    while (i < n) {
        arr4[k] = arr[i];
        i++;
        k++;
    }
    while (j < m) {
        arr4[k] = arr2[j];
        j++;
        k++;
    }

    // print the array
    printf("Merged Array: ");
    for (int i = 0; i < n + m + k; i++) {
        printf("%d ", arr4[i]);
    }
    printf("\n");

    return 0;
}

