#define MAX_FILENAME_LEN 256

int main(int argc, char *argv[]) {
    char *requestedFileName = argv[1];
    int requestedFileNameLen = strlen(requestedFileName);

    char *restrictedSafeDirectory = "/safe/";

    // read the requested file from the safe directory
    char fileNameBuffer[MAX_FILENAME_LEN];
    strcpy(fileNameBuffer, restrictedSafeDirectory);
    strcat(fileNameBuffer, requestedFileName);

    FILE *file = fopen(fileNameBuffer, "r");
    if (file == NULL) {
        printf("Error: Could not open file %s\n", fileNameBuffer);
        return 1;
    }

    // read the file contents into a buffer
    char *fileContents = NULL;
    size_t fileSize = 0;
    fseek(file, 0, SEEK_END);
    fileSize = ftell(file);
    fseek(file, 0, SEEK_SET);
    fileContents = (char *)malloc(fileSize + 1);
    fread(fileContents, fileSize, 1, file);
    fileContents[fileSize] = '\0';

    // print the file contents
    printf("%s", fileContents);

    fclose(file);
    free(fileContents);

    return 0;
}

