#include <stdio.h>
#include <stdlib.h>

int main(int argc, char *argv[]) {
    // malloc a large buffer and copy in 100 characters from stdin
    char* buf = malloc(1024);
    int i = 0;
    while (i < 100) {
        buf[i] = getchar();
        i++;
    }
    buf[i] = '\0';
    printf("buf: %s\n", buf);

    // free the buffer
    free(buf);

    return 0;
}

#include <stdio.h>
#include <stdlib.h>

int main(int argc, char *argv[]) {
    // malloc a large buffer and copy in 100 characters from stdin
    char* buf = malloc(1024);
    int i = 0;
    while ((buf[i++] = getchar())!= EOF) {
        if (i == 1024) {
            buf = realloc(buf, 2 * 1024);
        }
    }
    buf[i - 1] = '\0';

    // print out the buffer
    printf("%s\n", buf);

    // free the buffer
    free(buf);

    return 0;
}

#include <stdio.h>
#include <stdlib.h>

int main(int argc, char *argv[]) {
    // malloc a large buffer and copy in 100 characters from stdin
    char* buf = malloc(1024);
    if (buf == NULL) {
        fprintf(stderr, "malloc failed\n");
        return 1;
    }
    int i = 0;
    while (i < 100) {
        int c = getchar();
        if (c == EOF) {
            break;
        }
        buf[i++] = c;
    }
    buf[i] = '\0';
    printf("buf = %s\n", buf);
    free(buf);
    return 0;
}

