#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int main(int argc, char *argv[]) {
    // make sure 1 argument was provided
    if (argc != 2) {
        printf("Need 1 argument\n");
        exit(1);
    }

    // get the argument
    char *input = argv[1];

    // print the argument
    printf("'%s'\n", input);

    // trim the trailing whitespace
char *end = input + strlen(input) - 1;
    while (end >= input && (*end =='' || *end == '\t' || *end == '\n' || *end == '\r')) {
        *end = '\0';
        end--;
    }

    // print the trimmed argument
    printf("'%s'\n", input);

    // trim the leading whitespace
    char *start = input;
    while (*start =='' || *start == '\t' || *start == '\n' || *start == '\r') {
        start++;
    }

    // print the trimmed argument
    printf("'%s'\n", start);

    // trim the leading and trailing whitespace
    char *trim = input;
    while (*trim =='' || *trim == '\t' || *trim == '\n' || *trim == '\r') {
        trim++;
    }
    end = trim + strlen(trim) - 1;
    while (end >= trim && (*end =='' || *end == '\t' || *end == '\n' || *end == '\r')) {
        *end = '\0';
        end--;
    }

    // print the trimmed argument
    printf("'%s'\n", trim);

    // trim the leading and trailing whitespace
    char *trim2 = input;
    while (*trim2 =='' || *trim2 == '\t' || *trim2 == '\n' || *trim2 == '\r') {
        trim2++;
    }
    end = trim2 + strlen(trim2) - 1;
    while (end >= trim2 && (*end =='' || *end == '\t' || *end == '\n' || *end == '\r')) {
        *end = '\0';
        end--;
    }
    start = trim2;
    while (*start =='' || *start == '\t' || *start == '\n' || *start == '\r') {
        start++;
    }

    // print the trimmed argument
    printf("'%s'\n", start);

    return 0;
}

