#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <limits.h>

#define FILENAME "data.csv"

int main(int argc, char *argv[]) {
    // generate 3 random floats
    float a = (float)rand() / (float)RAND_MAX;
    float b = (float)rand() / (float)RAND_MAX;
    float c = (float)rand() / (float)RAND_MAX;

    // convert to string
    char a_str[100], b_str[100], c_str[100];
    sprintf(a_str, "%f", a);
    sprintf(b_str, "%f", b);
    sprintf(c_str, "%f", c);

    // write to file
    FILE *fp = fopen(FILENAME, "w");
    fprintf(fp, "%s,%s,%s\n", a_str, b_str, c_str);
    fclose(fp);

    // read from file
    FILE *fp_read = fopen(FILENAME, "r");
    char line[100];
    fgets(line, 100, fp_read);
    fclose(fp_read);

    // split string
    char *token;
    token = strtok(line, ",");
    float a_read = atof(token);
    token = strtok(NULL, ",");
    float b_read = atof(token);
    token = strtok(NULL, ",");
    float c_read = atof(token);

    // print results
    printf("a: %f\n", a);
    printf("b: %f\n", b);
    printf("c: %f\n", c);
    printf("a_read: %f\n", a_read);
    printf("b_read: %f\n", b_read);
    printf("c_read: %f\n", c_read);

    // check if a, b, c are equal
    if (a == a_read && b == b_read && c == c_read) {
        printf("a, b, c are equal\n");
    } else {
        printf("a, b, c are not equal\n");
    }

    return 0;
}

