#include <stdio.h>
#include <stdlib.h>
#include <unistd.h>
#include <fcntl.h>

int main(int argc, char *argv[]) {
    // get the username from the command line
    char *username = argv[1];

    // get the userinfo from the system
    char command[100] = {0};
    sprintf(command, "getent passwd %s", username);
    FILE *fp = popen(command, "r");
    if (fp == NULL) {
        perror("popen");
        exit(1);
    }

    char line[100] = {0};
    if (fgets(line, 100, fp) == NULL) {
        printf("No such user!\n");
        exit(1);
    }

    // get the user id
    char *uid = strtok(line, ":");
    uid = strtok(NULL, ":");

    // get the user group id
    char *gid = strtok(NULL, ":");
    gid = strtok(NULL, ":");

    // get the user home directory
    char *home = strtok(NULL, ":");
    home = strtok(NULL, ":");

    // get the user shell
    char *shell = strtok(NULL, ":");

    // get the user password
    char *passwd = strtok(NULL, ":");

    // get the user info
    printf("uid: %s\ngid: %s\nhome: %s\nshell: %s\npasswd: %s\n", uid, gid, home, shell, passwd);

    // close the file
    pclose(fp);

    return 0;
}

