#include <stdio.h>
#include <stdlib.h>
#include <unistd.h>
#include <fcntl.h>

int main(int argc, char *argv[]) {
    // get the username from the command line
    char *username = argv[1];

    // get the userinfo from the system
    char command[100] = {0};
    sprintf(command, "getent passwd %s", username);
    FILE *fp = popen(command, "r");
    if (fp == NULL) {
        printf("Error opening pipe!\n");
        return 1;
    }

    char line[100] = {0};
    if (fgets(line, 100, fp) == NULL) {
        printf("Error reading from pipe!\n");
        return 1;
    }

    // get the UID and GID from the userinfo
    char *uid = strtok(line, ":");
    char *gid = strtok(NULL, ":");
    char *home = strtok(NULL, ":");
    char *shell = strtok(NULL, ":");

    // get the group info from the system
    sprintf(command, "getent group %s", gid);
    fp = popen(command, "r");
    if (fp == NULL) {
        printf("Error opening pipe!\n");
        return 1;
    }

    if (fgets(line, 100, fp) == NULL) {
        printf("Error reading from pipe!\n");
        return 1;
    }

    // get the group name from the group info
    char *groupname = strtok(line, ":");

    // print the results
    printf("Username: %s\n", username);
    printf("UID: %s\n", uid);
    printf("GID: %s\n", gid);
    printf("Home directory: %s\n", home);
    printf("Shell: %s\n", shell);
    printf("Group name: %s\n", groupname);

    // close the pipe
    pclose(fp);

    return 0;
}

