#include <stdio.h>
#include <stdlib.h>
#include <unistd.h>
#include <fcntl.h>

int main(int argc, char *argv[]) {
    // get a directory name from the command line
    char *dirname = argv[1];

    // list the contents of that directory using "ls" command line utility
    char cmd[256] = {0};
    sprintf(cmd, "ls %s", dirname);
    system(cmd);

    // get the file descriptor of the directory
    int dirfd = open(dirname, O_RDONLY);
    if (dirfd < 0) {
        perror("open");
        exit(EXIT_FAILURE);
    }

    // get the file descriptor of the "ls" command
    int lsfd = open("/bin/ls", O_RDONLY);
    if (lsfd < 0) {
        perror("open");
        exit(EXIT_FAILURE);
    }

    // create a pipe
    int pipefd[2];
    if (pipe(pipefd) < 0) {
        perror("pipe");
        exit(EXIT_FAILURE);
    }

    // fork a child process
    pid_t pid = fork();
    if (pid < 0) {
        perror("fork");
        exit(EXIT_FAILURE);
    }

    // child process
    if (pid == 0) {
        // close the read end of the pipe
        close(pipefd[0]);

        // dup2() the write end of the pipe to the read end of the directory
        dup2(pipefd[1], dirfd);

        // close the write end of the pipe
        close(pipefd[1]);

        // close the read end of the directory
        close(dirfd);

        // execute the "ls" command
        execl("/bin/ls", "ls", NULL);
        perror("execl");
        exit(EXIT_FAILURE);
    }

    // parent process
    // close the write end of the pipe
    close(pipefd[1]);

    // read the output of the "ls" command
    char buf[1024];
    while (read(pipefd[0], buf, sizeof(buf)) > 0) {
        printf("%s", buf);
    }

    // close the read end of the pipe
    close(pipefd[0]);

    // wait for the child process to finish
    int status;
    if (waitpid(pid, &status, 0) < 0) {
        perror("waitpid");
        exit(EXIT_FAILURE);
    }

    // exit with the exit status of the child process
    exit(WEXITSTATUS(status));
}

