#include <limits.h>

int main(int argc, char *argv[]) {
    // generate a random integer larger than 1000
    int random = rand() % 1000 + 1000;
    printf("random: %d\n", random);

    // print out the number in reverse order
    int reverse = 0;
    while (random > 0) {
        reverse = reverse * 10 + random % 10;
        random /= 10;
    }
    printf("reverse: %d\n", reverse);

    // print out the number in binary
    int binary = 0;
    int i = 0;
    while (reverse > 0) {
        binary += (reverse % 2) * pow(10, i);
        reverse /= 2;
        i++;
    }
    printf("binary: %d\n", binary);

    // print out the number in hexadecimal
    int hexadecimal = 0;
    i = 0;
    while (reverse > 0) {
        hexadecimal += (reverse % 16) * pow(10, i);
        reverse /= 16;
        i++;
    }
    printf("hexadecimal: %d\n", hexadecimal);

    return 0; 0;
}

