#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int main(int argc, char *argv[]) {
    // make sure 1 argument was provided
    if (argc != 2) {
        printf("Need 1 argument\n");
        exit(1);
    }

    // get the argument
    char *input = argv[1];

    // print the argument
    printf("'%s'\n", input);

    // trim the trailing whitespace
    int len = strlen(input);
    while (len > 0 && input[len-1] == ' ') {
        input[len-1] = '\0';
        len--;
    }

    // print the trimmed argument
    printf("'%s'\n", input);

    // reverse the argument
    char *output = malloc(len + 1);
    for (int i = 0; i < len; i++) {
        output[i] = input[len-i-1];
    }
    output[len] = '\0';

    // print the reversed argument
    printf("'%s'\n", output);

    return 0;   }

    // print the reversed argument
    printf("'%s'\n", output);

    return 0;
}

#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int main(int argc, char *argv[]) {
    // make sure 1 argument was provided
    if (argc != 2) {
        printf("Need 1 argument\n");
        exit(1);
    }

    // get the argument
    char *input = argv[1];

    // print the argument
    printf("'%s'\n", input);

    // trim the trailing whitespace
    char *end = input + strlen(input) - 1;
    while (end > input && isspace(*end)) {
        end--;
    }
    *(end + 1) = 0;

    // print the argument
    printf("'%s'\n", input);

    // trim the leading whitespace
    while (*input && isspace(*input)) {
        input++;
    }

    // print the argument
    printf("'%s'\n", input);

    // print the length
    printf("'%zu'\n", strlen(input));

    return 0;  return 0;
}

