        printf("Need 1 argument\n");
        exit(1);
    }

    // get the argument
    char *input = argv[1];

    // print the argument
    printf("'%s'\n", input);

    // trim the trailing whitespace
    int len = strlen(input);
    while (len > 0 && input[len - 1] == ' ') {
        input[len - 1] = '\0';
        len--;
    }

    // print the argument
    printf("'%s'\n", input);

    // trim the leading whitespace
    while (input[0] == ' ') {
        input++;
    }

    // print the argument
    printf("'%s'\n", input);

    // trim the leading and trailing whitespace
    char *output = malloc(len + 1);
    int i = 0;
    while (input[i] == ' ') {
        i++;
    }
    int j = 0;
    while (input[i] != '\0') {
        output[j] = input[i];
        i++;
        j++;
    }
    while (output[j - 1] == ' ') {
        j--;
    }
    output[j] = '\0';

    // print the argument
    printf("'%s'\n", output);

    // free the memory
    free(output);

    return 0;/week03/ex02/main.c

