#define FILENAME "data.csv"

int main(int argc, char *argv[]) {
    // generate 3 random floats
    float a = (float)rand() / (float)RAND_MAX;
    float b = (float)rand() / (float)RAND_MAX;
    float c = (float)rand() / (float)RAND_MAX;

    // convert to string
    char a_str[10];
    char b_str[10];
    char c_str[10];
    sprintf(a_str, "%f", a);
    sprintf(b_str, "%f", b);
    sprintf(c_str, "%f", c);

    // write to file
    FILE *fp = fopen(FILENAME, "w");
    fprintf(fp, "%s,%s,%s\n", a_str, b_str, c_str);
    fclose(fp);

    // read from file
    fp = fopen(FILENAME, "r");
    char line[100];
    fgets(line, 100, fp);
    fclose(fp);

    // split line into tokens
    char *token = strtok(line, ",");
    char *tokens[3];
    int i = 0;
    while (token != NULL) {
        tokens[i] = token;
        token = strtok(NULL, ",");
        i++;
    }

    // convert tokens to floats
    float a_read = atof(tokens[0]);
    float b_read = atof(tokens[1]);
    float c_read = atof(tokens[2]);

    // print results
    printf("a = %f\n", a_read);
    printf("b = %f\n", b_read);
    printf("c = %f\n", c_read);

    return 0;/src/main/java/com/example/demo/controller/HelloController.java
