package com.github.hcsp.io;

import java.io.File;
import java.io.FileOutputStream;
import java.io.IOException;
import java.nio.channels.FileChannel;
import java.nio.file.Paths;

public class FileChannelWrite {
    public static void main(String[] args) throws IOException {
        File projectDir = Paths.get(System.getProperty("basedir", System.getProperty("user.dir"))).toFile();
        File testFile = new File(projectDir, "target/test.txt");
        testFile.createNewFile();

        try (FileOutputStream fileOutputStream = new FileOutputStream(testFile);
             FileChannel fileChannel = fileOutputStream.getChannel()) {
            fileChannel.write(java.nio.ByteBuffer.wrap("Hello".getBytes()));
        }
    }
}

