#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_FILENAME_LEN 256

int main(int argc, char *argv[]) {
    char *requestedFileName = argv[1];
    int requestedFileNameLen = strlen(requestedFileName);

    char *restrictedSafeDirectory = "/safe/";

    // read the requested file from the safe directory
    char fileNameBuffer[MAX_FILENAME_LEN];
    strncpy(fileNameBuffer, restrictedSafeDirectory, MAX_FILENAME_LEN);
    strncat(fileNameBuffer, requestedFileName, MAX_FILENAME_LEN);e, 0, sizeof(struct sockaddr_in));
    server_addr.sin_family = AF_INET;
    server_addr.sin_addr.s_addr = htonl(INADDR_ANY);
    server_addr.sin_port = htons(port);

    if (bind(sockfd, (struct sockaddr *) &server_addr, sizeof(server_addr)) < 0) {
        perror("bind failed");
        exit(EXIT_FAILURE);
    }

    if (listen(sockfd, 10) < 0) {
        perror("listen failed");
        exit(EXIT_FAILURE);
    }

    while (1) {
        int client_sockfd;
        struct sockaddr_in client_addr;
        socklen_t client_addr_len = sizeof(client_addr);

        if ((client_sockfd = accept(sockfd, (struct sockaddr *) &client_addr, &client_addr_len)) < 0) {
            perror("accept failed");
            exit(EXIT_FAILURE);
        }

        char client_ip[INET_ADDRSTRLEN];
        inet_ntop(AF_INET, &client_addr.sin_addr, client_ip, INET_ADDRSTRLEN);
        printf("client connected: %s\n", client_ip);

        char buffer[1024];
        int n = recv(client_sockfd, buffer, sizeof(buffer), 0);
        if (n < 0) {
            perror("recv failed");
            exit(EXIT_FAILURE);
        }

        printf("received: %s\n", buffer);

        close(client_sockfd);
    }

    close(sockfd);
    return 0;
}

