#include <stdio.h>
#include <stdlib.h>

int main(int argc, char *argv[]) {
    // malloc a large buffer and copy in 100 characters from stdin
    char* buf = malloc(1024);
    fread(buf, 1, 100, stdin);
    // print the buffer
    printf("%s", buf);
    // free the buffer
    free(buf);
    return 0;t(1);
    }
    // get the file descriptor
    int fd = open(argv[1], O_RDONLY);
    if (fd == -1) {
        perror("open");
        exit(1);
    }
    // get the file size
    struct stat st;
    if (fstat(fd, &st) == -1) {
        perror("fstat");
        exit(1);
    }
    // allocate a buffer
    char* buf = malloc(st.st_size);
    if (buf == NULL) {
        perror("malloc");
        exit(1);
    }
    // read the file
    if (read(fd, buf, st.st_size) == -1) {
        perror("read");
        exit(1);
    }
    // print the buffer
    printf("%s", buf);
    // free the buffer
    free(buf);
    // close the file
    if (close(fd) == -1) {
        perror("close");
        exit(1);
    }
    return 0;
}

