#include <fcntl.h>

int main(int argc, char *argv[]) { 
    char* important_config = "important_config";

    // save the important config to a file
    int fd = open(important_config, O_CREAT | O_WRONLY, 0644);
    write(fd, important_config, sizeof(important_config));
    close(fd);

    // fork a child process
    pid_t pid = fork();
    if (pid == 0) {
        // child process
        // read the important config from the file
        fd = open(important_config, O_RDONLY);
        char buf[100];
        read(fd, buf, sizeof(buf));
        close(fd);
        printf("child process read: %s\n", buf);
    } else {
        // parent process
        // read the important config from the file
        fd = open(important_config, O_RDONLY);
        char buf[100];
        read(fd, buf, sizeof(buf));
        close(fd);
        printf("parent process read: %s\n", buf);
    }

    return 0;   }

    return 0;
}

