#include <stdio.h>
#include <stdlib.h>
#include <unistd.h>
#include <fcntl.h>

int main(int argc, char *argv[]) { 
    char* important_config = "important_config";

    // save the important config to a file, ensuring it is not world-writable
    int fd = open(important_config, O_WRONLY | O_CREAT | O_EXCL, 0600);
    if (fd == -1) {
        perror("open");
        exit(1);
    }
    if (write(fd, important_config, strlen(important_config)) == -1) {
        perror("write");
        exit(1);
    }
    if (close(fd) == -1) {
        perror("close");
        exit(1);
    }

    // create a new process
    pid_t pid = fork();
    if (pid == -1) {
        perror("fork");
        exit(1);
    }

    // the child process will read the important config file
    if (pid == 0) {
        int fd = open(important_config, O_RDONLY);
        if (fd == -1) {
            perror("open");
            exit(1);
        }
        char buf[100];
        if (read(fd, buf, sizeof(buf)) == -1) {
            perror("read");
            exit(1);
        }
        if (close(fd) == -1) {
            perror("close");
            exit(1);
        }
        printf("child: %s\n", buf);
        exit(0);
    }

    // the parent process will print the important config
    int fd = open(important_config, O_RDONLY);
    if (fd == -1) {
        perror("open");
        exit(1);
    }
    char buf[100];
    if (read(fd, buf, sizeof(buf)) == -1) {
        perror("read");
        exit(1);
    }
    if (close(fd) == -1) {
        perror("close");
        exit(1);
    }
    printf("parent: %s\n", buf);

    // wait for the child to finish
    if (wait(NULL) == -1) {
        perror("wait");
        exit(1);
    }

    return 0; 0;
}

