#include <stdio.h>
#include <stdlib.h>
#include <unistd.h>
#include <fcntl.h>

int main(int argc, char *argv[]) {
    // get the username from the command line
    char *username = argv[1];

    // get the userinfo from the system
    char command[100] = {0};
    sprintf(command, "getent passwd %s", username);
    char *userinfo = popen(command, "r");

    // get the uid and gid from the userinfo
    char *uid = strtok(userinfo, ":");
    char *gid = strtok(NULL, ":");
    char *home = strtok(NULL, ":");

    // create a new process
    pid_t pid = fork();

    // if the process is the child process
    if (pid == 0) {
        // set the uid and gid
        setuid(atoi(uid));
        setgid(atoi(gid));

        // change the working directory
        chdir(home);

        // open the file
        int fd = open("flag", O_RDONLY);

        // read the file
        char flag[100] = {0};
        read(fd, flag, 100);

        // print the flag
        printf("%s\n", flag);
