#include <stdio.h>
#include <stdlib.h>
#include <unistd.h>
#include <fcntl.h>

int main(int argc, char *argv[]) {
    // get a directory name from the command line
    char *dirname = argv[1];

    // list the contents of that directory using "ls" command line utility
    char cmd[256] = {0};
    sprintf(cmd, "ls %s", dirname);  printf("Error: %s\n", strerror(errno));
        exit(1);
    }

    // create a pipe
    int pipefd[2];
    if (pipe(pipefd) == -1) {
        printf("Error: %s\n", strerror(errno));
        exit(1);
    }

    // create a child process
    pid_t pid = fork();
    if (pid == -1) {
        printf("Error: %s\n", strerror(errno));
        exit(1);
    }

    // close the write end of the pipe
    if (pid == 0) {
        close(pipefd[1]);

        // read from the pipe
        char buf[1024] = {0};
        int n = read(pipefd[0], buf, sizeof(buf));
        if (n == -1) {
            printf("Error: %s\n", strerror(errno));
            exit(1);
        }

        // print the contents of the pipe
        printf("Received %d bytes from the pipe: %s\n", n, buf);

        // close the read end of the pipe
        close(pipefd[0]);
    } else {
        // close the read end of the pipe
        close(pipefd[0]);

        // write to the pipe
        char *msg = "Hello, world!";
        int n = write(pipefd[1], msg, strlen(msg));
        if (n == -1) {
            printf("Error: %s\n", strerror(errno));
            exit(1);
        }

        // close the write end of the pipe
        close(pipefd[1]);

        // wait for the child process to finish
        wait(NULL);
    }
}

